  `thumb` varchar(100) NOT NULL DEFAULT '',
  `property1` varchar(200) NOT NULL DEFAULT '',
  `property2` varchar(200) NOT NULL DEFAULT '',
  `property3` varchar(200) NOT NULL DEFAULT '',
