INSERT INTO `wm6_ad` VALUES (8,'分类底部广告','','javascript:;','5b212157a9af7.jpg',1),(9,'资料下载-banner',NULL,'javascript:;','5910675e5e508.jpg',99),(10,'在线留言-banner','','javascript:;','5b19439c1ed8d.jpg',99),(13,'新闻详情-banner','','javascript:;','5b2b21552968b.jpg',99),(15,'产品详情-banner','','javascript:;','5b2b21451fb0e.jpg',99),(16,'网站地图-banner','','javascript:;','5b2b216271dea.jpg',99),(18,'PC版首页横幅广告','','/feedback.html','5b2b1e4b2cbb7.jpg',2),(23,'PC版首页简介','微梦机械设计有限公司拥有从事工业流体温度控制技术10多年经验的技术和服务团队。\r\n公司一直致力于高性能工业温度控制设备的研发、生产、销售、服务和技术咨询，是一家生产节能工业温度控制设备和提供温控系统解决方案的企业，公司团队在技术领域的的不断创新，注重客户的使用感受，受到业界和使用客户的一致认可。','/about.html','5e6b168300a30.jpg',5),(27,'手机首页横幅','','javascript:;','5b2b21c759fd0.jpg',8),(29,'自主研发，实力雄厚','我们拥有专业从事工业流体温度控制技术10多年经验的技术和服务团队，一直致力于高性能工业温度控制设备的研发、生产、销售、服务和技术咨询。','javascript:;','5b227383a5f36.jpg',3),(30,'技术保障，经久耐用','在引进、消化国外先进技术的基础上自主研究开发，不断创新进取，使我们始终处于国内温控设备制造业领先水平。','javascript:;','5b2273a269b24.jpg',3),(31,'专业团队，售后无忧','品质优先、用户满意是我们的企业理念。始终坚持不断突破、不断创新，向国际现代化温控设备制造企业迈进。我司全体员工24小时为您服务！','javascript:;','5b2273bb26338.jpg',3),(32,'4大优势-1','','javascript:;','5e6b153abbf0e.png',4),(33,'4大优势-2','','javascript:;','5e6b155f47ef8.png',4),(34,'4大优势-3','','javascript:;','5e6b156ff2a74.png',4),(35,'4大优势-4','','javascript:;','5e6b158050c2d.png',4),(36,'4大优势-0','','javascript:;','5e6b1195958d6.png',4);
